INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'ALTERNATING_DUMBBELL_CHEST_PRESS', 'STRENGTH', '{chest,triceps,shoulders,push,unilateral,stability}', '{dumbbells,bench}', 'A dumbbell bench press performed by alternating arms - pressing one dumbbell up while the other is held at chest level. This unilateral pattern increases core engagement and shoulder stability demands while addressing strength imbalances between sides. The constant tension on the non-pressing arm builds isometric endurance.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'ALTERNATING_DUMBBELL_CHEST_PRESS_ON_SWISS_BALL', 'STRENGTH', '{chest,triceps,shoulders,core,push,unilateral,stability}', '{dumbbells,swiss ball}', 'An alternating dumbbell press performed on a swiss ball instead of a bench. The unstable surface dramatically increases core and stabilizer activation while the alternating pattern challenges balance and anti-rotation strength. Excellent for building functional pressing strength and total-body stability.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'BARBELL_BENCH_PRESS', 'STRENGTH', '{chest,triceps,shoulders,push,compound}', '{barbell,bench}', 'The king of upper body exercises. Lying supine on a bench, lower the barbell to the chest and press it back up. This compound movement primarily targets the pectoralis major, anterior deltoids, and triceps. The barbell allows for maximum loading and is the standard for building upper body pressing strength and mass.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'BARBELL_BOARD_BENCH_PRESS', 'STRENGTH', '{chest,triceps,shoulders,push,power}', '{barbell,bench,boards}', 'A partial range bench press where boards (typically 1-5 inches thick) are placed on the chest to limit depth. This variation overloads the lockout portion, builds tricep strength, and allows for supra-maximal loading. Popular in powerlifting for building top-end strength and overcoming sticking points.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
