INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CARRY', 'BAR_HOLDS', 'STRENGTH', '{grip,forearms,back,core,carry,isometric}', '{barbell}', 'An isometric hold where a loaded barbell is gripped and held statically, typically in a deadlift position. This exercise builds massive grip strength, forearm endurance, and total-body stability. Can be held at various heights or during the lockout of a deadlift. Essential for improving grip strength for pulling exercises.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CARRY', 'DUMBBELL_WAITER_CARRY', 'STRENGTH', '{shoulders,core,stability,carry,grip,unilateral}', '{dumbbells}', 'A single-arm overhead carry where a dumbbell is held like a waiter carrying a tray - arm fully extended overhead with elbow locked out and palm facing up. This challenges shoulder stability, core anti-lateral flexion, and grip strength. Excellent for building overhead strength and stability.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CARRY', 'FARMERS_CARRY', 'STRENGTH', '{grip,forearms,core,traps,full body,carry}', '{dumbbells}', 'A loaded carry exercise where heavy weights (dumbbells, kettlebells, or farmer''s walk handles) are held at the sides while walking. This fundamental carry builds grip strength, core stability, trap development, and total-body strength. One of the most functional exercises for real-world strength and conditioning.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CARRY', 'FARMERS_CARRY_ON_TOES', 'STRENGTH', '{grip,forearms,core,traps,calves,carry,balance}', '{dumbbells}', 'A farmer''s carry variation performed while walking on the toes (plantar flexed ankles). This adds a significant calf and ankle stability challenge while maintaining all the grip and core benefits of standard farmer''s carries. Excellent for developing calf endurance and ankle stability under load.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
