INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CARRY', 'FARMERS_WALK', 'STRENGTH', '{grip,forearms,core,traps,full body,carry}', '{dumbbells}', 'Another name for farmer''s carry. Walking while holding heavy weights at the sides. This exercise builds functional, real-world strength including grip endurance, core stability, trap development, and mental toughness. A staple in strongman training and excellent for overall strength development.');

