INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_RAISE', 'SINGLE_LEG_HIP_RAISE_WITH_FOOT_ON_MEDICINE_BALL', 'STRENGTH', '{glutes,hips,hamstrings,core,unilateral,stability}', '{medicine ball}', 'A single-leg hip raise with the working foot on a medicine ball. The spherical, unstable medicine ball creates extreme balance demands. The ball can roll in any direction, requiring constant micro-adjustments and maximal core engagement. This is one of the most challenging single-leg bridge variations. Builds elite-level glute strength, balance, and proprioception.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
