-- Garmin Exercises: HIP_STABILITY

-- Total exercises: 35

-- Generated: 2025-11-18T02:24:16.081Z


INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_STABILITY', 'BAND_SIDE_LYING_LEG_RAISE', 'STRENGTH', '{glutes,hips,hip abductors,gluteus medius}', '{resistance band}', 'A side-lying leg raise with resistance band around legs or ankles. Lie on your side and lift the top leg against band resistance. This exercise targets the gluteus medius and minimus - critical muscles for hip stability, knee alignment, and injury prevention. The band provides constant tension throughout the movement. Essential for runners, athletes, and anyone wanting strong, stable hips.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_STABILITY', 'DEAD_BUG', 'STRENGTH', '{core,abs,hip flexors,stability}', '{bodyweight}', 'A fundamental core stability exercise - lie on your back, extend opposite arm and leg while keeping lower back flat. The name comes from the position resembling a dead bug on its back. This exercise teaches anti-extension core control, coordinates opposite limbs, and builds deep core stability. Essential for lower back health and functional movement. One of the best exercises for core stability and preventing lower back pain.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_STABILITY', 'EXTERNAL_HIP_RAISE', 'STRENGTH', '{glutes,hips,hip abductors,external rotators}', '{bodyweight}', 'A hip raise with external rotation - lift the leg while rotating it outward. This targets the hip external rotators (deep six muscles including piriformis) and gluteus medius. These muscles are crucial for hip stability, proper gait mechanics, and preventing knee valgus. Excellent for addressing hip weakness and improving movement quality. Important for injury prevention and athletic performance.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_STABILITY', 'FIRE_HYDRANT_KICKS', 'STRENGTH', '{glutes,hips,hip abductors,gluteus medius}', '{bodyweight}', 'From hands and knees, lift one leg out to the side (like a dog at a fire hydrant), then extend with a kicking motion. This exercise targets the gluteus medius and hip abductors through abduction and extension. The name comes from the distinctive sideways leg position. Excellent for hip stability, glute activation, and improving lateral hip strength. Popular in physical therapy and functional training.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_STABILITY', 'HIP_CIRCLES', 'STRENGTH', '{glutes,hips,hip abductors,core,mobility}', '{bodyweight}', 'Circular hip movements performed standing or from quadruped position. Rotate the leg in large circles, working through the full range of hip motion. This exercise improves hip mobility, activates all hip muscles in multiple planes, and enhances coordination. The circular motion trains the hip through flexion, extension, abduction, and rotation. Excellent warm-up exercise and for improving hip function.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_STABILITY', 'INNER_THIGH_LIFT', 'STRENGTH', '{hips,hip adductors,inner thighs}', '{bodyweight}', 'Lie on your side with bottom leg straight and top leg crossed over. Lift the bottom (inner) leg off the ground. This isolates the hip adductors (inner thigh muscles including adductor longus, brevis, and magnus). These muscles are often neglected but crucial for hip stability, athletic performance, and injury prevention. Excellent for building inner thigh strength and balanced hip development.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_STABILITY', 'LATERAL_WALKS_WITH_BAND_AT_ANKLES', 'STRENGTH', '{glutes,hips,hip abductors,gluteus medius}', '{resistance band}', 'Lateral band walks - place resistance band around ankles, maintain athletic stance, and walk sideways. This exercise is one of the most effective for activating the gluteus medius and hip abductors. The constant tension prevents knee collapse and builds lateral hip strength. Essential for athletes, runners, and anyone with knee issues. Widely used in physical therapy and sports performance training.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_STABILITY', 'LYING_ABDUCTION_STRETCH', 'STRENGTH', '{hips,hip abductors,flexibility}', '{bodyweight}', 'A lying hip abduction stretch - lie on your back and allow legs to fall open to the sides. This stretches the hip adductors while gently activating the abductors. The lying position reduces load and allows for a more relaxed stretch. Good for improving hip mobility and range of motion. Can be used as a warm-up or cool-down exercise.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_STABILITY', 'PRETZEL_SIDE_KICK', 'STRENGTH', '{glutes,hips,hip abductors,core}', '{bodyweight}', 'A Pilates-inspired exercise - lie on your side in a "pretzel" position (one leg bent in front, one behind) and kick the top leg. This unique position targets the glutes and hip abductors from an unusual angle. The pretzel setup challenges balance and core stability while isolating the hip muscles. Popular in Pilates and barre workouts for glute sculpting and hip mobility.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_STABILITY', 'PRONE_HIP_INTERNAL_ROTATION', 'STRENGTH', '{hips,hip internal rotators,glutes}', '{bodyweight}', 'Lie face down and rotate the leg inward from the hip. This exercise targets the hip internal rotators - muscles often weak in athletes and desk workers. Internal rotation strength is crucial for proper gait mechanics, athletic performance, and injury prevention. Addresses common imbalances and improves hip function. Important for balanced hip development.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_STABILITY', 'QUADRUPED', 'STRENGTH', '{core,glutes,shoulders,stability}', '{bodyweight}', 'The basic quadruped position - on hands and knees with neutral spine. This fundamental position is the starting point for many stability exercises. Holding this position builds core stability, shoulder stability, and teaches proper spinal alignment. Essential base position for exercises like bird dogs, fire hydrants, and hip extensions. Foundational for functional movement training.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_STABILITY', 'QUADRUPED_HIP_EXTENSION', 'STRENGTH', '{glutes,hips,hamstrings,core}', '{bodyweight}', 'From hands and knees, extend one leg straight back. This isolation exercise targets the glutes and hamstrings while requiring core stability to prevent lower back arching. The quadruped position reduces load compared to standing variations. Excellent for glute activation, teaching proper hip extension, and building mind-muscle connection. Popular in physical therapy and corrective exercise.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_STABILITY', 'QUADRUPED_WITH_LEG_LIFT', 'STRENGTH', '{glutes,hips,core,stability}', '{bodyweight}', 'From quadruped position, lift one leg (can be bent or straight). Similar to quadruped hip extension but with emphasis on maintaining stability. This exercise challenges balance, core anti-rotation strength, and glute activation simultaneously. The leg lift creates asymmetry that the core must resist. Excellent for functional stability and glute development.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_STABILITY', 'SIDE_LYING_LEG_RAISE', 'STRENGTH', '{glutes,hips,hip abductors,gluteus medius}', '{bodyweight}', 'The classic side-lying leg raise - lie on your side and lift the top leg straight up. This is one of the most effective exercises for isolating the gluteus medius and hip abductors. Simple but highly effective for building hip stability and lateral strength. Essential exercise for runners, preventing knee injuries, and building strong hips. Can be progressed with ankle weights or resistance bands.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_STABILITY', 'SLIDING_HIP_ADDUCTION', 'STRENGTH', '{hips,hip adductors,inner thighs,core}', '{sliding discs}', 'Stand with one foot on a sliding disc and slide it inward (adduction). This dynamic exercise targets the hip adductors while the standing leg works for stability. The sliding motion creates constant tension and challenges balance. Excellent for building inner thigh strength, hip stability, and functional control. More challenging than static adduction exercises.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_STABILITY', 'STANDING_ADDUCTION', 'STRENGTH', '{hips,hip adductors,inner thighs}', '{bodyweight}', 'Stand on one leg and swing the other leg inward across the body. This standing adduction exercise targets the inner thighs while challenging balance. The standing position adds functional component and requires stability from the supporting leg. Good for building adductor strength in a more functional pattern. Can be progressed with ankle weights or cables.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_STABILITY', 'STANDING_CABLE_HIP_ABDUCTION', 'STRENGTH', '{glutes,hips,hip abductors,gluteus medius}', '{cable machine}', 'Stand sideways to cable machine with ankle attachment and lift leg away from machine. The cable provides constant tension throughout the movement, superior to bodyweight variations. Standing position adds balance and functional challenge. One of the best exercises for isolated hip abductor development. Essential for building strong, stable hips and preventing knee injuries.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_STABILITY', 'STANDING_HIP_ABDUCTION', 'STRENGTH', '{glutes,hips,hip abductors,gluteus medius}', '{bodyweight}', 'Stand on one leg and lift the other leg out to the side. The bodyweight version of standing cable abduction. This exercise builds hip abductor strength while challenging balance and stability. The standing position makes it more functional than lying variations. Excellent for injury prevention, improving gait mechanics, and building hip stability. Can be done anywhere without equipment.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_STABILITY', 'STANDING_REAR_LEG_RAISE', 'STRENGTH', '{glutes,hips,hamstrings}', '{bodyweight}', 'Stand on one leg and lift the other leg straight back. This standing hip extension exercise targets the glutes and hamstrings while requiring balance. The standing position makes it more functional and challenging than prone variations. Excellent for glute activation, improving single-leg stability, and developing functional posterior chain strength. Can progress with ankle weights.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_STABILITY', 'SUPINE_HIP_INTERNAL_ROTATION', 'STRENGTH', '{hips,hip internal rotators,core}', '{bodyweight}', 'Lie on your back and rotate legs inward from the hips. This supine variation allows for controlled hip internal rotation training. Internal rotators are often weak and imbalanced. This exercise improves hip mobility, addresses imbalances, and enhances overall hip function. Important for proper movement mechanics and injury prevention. Commonly used in mobility and corrective exercise programs.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_STABILITY', 'WEIGHTED_DEAD_BUG', 'STRENGTH', '{core,abs,hip flexors,stability}', '{dumbbells,medicine ball}', 'A weighted dead bug - hold dumbbells or medicine ball while performing the dead bug movement. The added weight increases core demand and builds greater anti-extension strength. This progression challenges the deep core stabilizers more intensely. Excellent for advanced core training and building exceptional stability. Only progress to weighted variations after mastering bodyweight form.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_STABILITY', 'WEIGHTED_EXTERNAL_HIP_RAISE', 'STRENGTH', '{glutes,hips,hip abductors,external rotators}', '{ankle weights,resistance band}', 'A weighted external hip raise using ankle weights or resistance band. The added resistance increases activation of hip external rotators and gluteus medius. This progression builds greater hip stability and rotational strength. Important for advanced hip strengthening and injury prevention. Progress to this after mastering bodyweight variations.');

