INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_STABILITY', 'WEIGHTED_SIDE_LYING_LEG_RAISE', 'STRENGTH', '{glutes,hips,hip abductors,gluteus medius}', '{ankle weights}', 'Side-lying leg raise with ankle weights. The added weight significantly increases gluteus medius and hip abductor activation. This is the primary progression from bodyweight side raises. Essential for building strong, resilient hips. Popular among runners and athletes for injury prevention and performance enhancement.');

