INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('INDOOR_BIKE', 'AIR_BIKE', 'STRENGTH', '{cardio,legs,full body,endurance}', '{air bike}', 'A fan-resistance stationary bike where pedaling and arm movement drive a large fan wheel. The harder you work, the more resistance you create. Air bikes engage both upper and lower body simultaneously, making them exceptionally challenging for conditioning. The self-regulating resistance makes them ideal for all-out interval training and metabolic conditioning.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('INDOOR_BIKE', 'ASSAULT_BIKE', 'STRENGTH', '{cardio,legs,full body,endurance,power}', '{assault bike}', 'A specific brand of air bike known for brutal conditioning workouts. Features moving arm handles and a large fan for resistance. Assault bikes are synonymous with high-intensity interval training and are a staple in CrossFit and functional fitness. The unlimited resistance ceiling makes them perfect for testing mental toughness and cardiovascular capacity.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('INDOOR_BIKE', 'STATIONARY_BIKE', 'STRENGTH', '{cardio,legs,quads,endurance}', '{stationary bike}', 'Traditional stationary cycling on an exercise bike or spin bike. Provides low-impact cardiovascular exercise that builds leg endurance, particularly in the quadriceps and glutes. Adjustable resistance allows for varied intensity from recovery rides to high-intensity intervals. Excellent for cardiovascular health without the joint stress of running.');
