INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CHOP', 'CABLE_PULL_THROUGH', 'STRENGTH', '{glutes,hamstrings,hips,core,hinge pattern}', '{cable machine}', 'A hip hinge exercise using a cable positioned low between the legs. Pull the cable through by extending the hips, similar to a kettlebell swing but more controlled. This posterior chain exercise builds glute and hamstring strength while teaching proper hip hinge mechanics. Despite the name, this is more of a hinge than a traditional rotational chop.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CHOP', 'CABLE_ROTATIONAL_LIFT', 'STRENGTH', '{core,obliques,rotation,full body}', '{cable machine}', 'A rotational movement starting low and lifting diagonally across the body to finish high (opposite of a chop). Using a cable machine, rotate from a low position on one side to an overhead position on the opposite side. Builds rotational power through the entire kinetic chain. Excellent for athletes requiring rotational strength.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CHOP', 'CABLE_WOODCHOP', 'STRENGTH', '{core,obliques,rotation}', '{cable machine}', 'The classic cable chop movement, mimicking an axe swing. Starting with cable high, pull diagonally down and across the body, rotating through the hips and core. This fundamental rotational exercise develops core strength, power transfer, and teaches proper sequencing for rotational movements. Essential for rotational athletes.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CHOP', 'CROSS_CHOP_TO_KNEE', 'STRENGTH', '{core,abs,obliques,rotation,balance}', '{bodyweight}', 'A dynamic bodyweight chop where you bring the knee up while simultaneously chopping the arms down across the body to meet the knee. This coordination exercise combines core rotation, balance, and hip flexion. Excellent for developing rotational core strength and single-leg stability.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CHOP', 'DUMBBELL_CHOP', 'STRENGTH', '{core,obliques,rotation,full body}', '{dumbbells}', 'A woodchop pattern performed with a single dumbbell held with both hands. Starting overhead on one side, chop diagonally down and across the body to the opposite hip. This free-weight variation builds rotational power and strength throughout the entire kinetic chain. Can be performed explosively or with control.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CHOP', 'HALF_KNEELING_ROTATION', 'STRENGTH', '{core,obliques,rotation,stability}', '{cable machine}', 'A rotational exercise performed in a half-kneeling position (one knee down, one foot forward). The stable base eliminates lower body compensation, forcing the core to generate rotation. This position is excellent for isolating rotational core strength and teaching proper rotation mechanics without excessive lower back involvement.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CHOP', 'HALF_KNEELING_ROTATIONAL_CHOP', 'STRENGTH', '{core,obliques,rotation,stability}', '{cable machine}', 'A cable chop performed from a half-kneeling position. The elevated hip position and stable base isolate the core rotational muscles while preventing compensation from the hips and legs. Excellent for developing pure rotational core strength and correcting rotation mechanics.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CHOP', 'HALF_KNEELING_ROTATIONAL_REVERSE_CHOP', 'STRENGTH', '{core,obliques,rotation,stability}', '{cable machine}', 'A reverse chop (low to high lift) performed from half-kneeling. Starting with the cable low on one side, rotate and lift diagonally to finish high on the opposite side. The half-kneeling position isolates the core and teaches proper upward rotational power generation.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CHOP', 'HALF_KNEELING_STABILITY_CHOP', 'STRENGTH', '{core,obliques,anti-rotation,stability}', '{cable machine}', 'An anti-rotation exercise in half-kneeling where you resist rotation while performing a chopping motion. The emphasis is on stability and control rather than explosive rotation. Builds core strength and teaches proper bracing against rotational forces.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CHOP', 'HALF_KNEELING_STABILITY_REVERSE_CHOP', 'STRENGTH', '{core,obliques,anti-rotation,stability}', '{cable machine}', 'A reverse chop with emphasis on stability and anti-rotation. From half-kneeling, perform a controlled lift from low to high while maintaining a stable core and resisting excessive rotation. Develops core control and stability under load.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CHOP', 'KNEELING_ROTATIONAL_CHOP', 'STRENGTH', '{core,obliques,rotation,stability}', '{cable machine}', 'A cable chop performed from a tall kneeling position (both knees down). This position eliminates all lower body contribution, forcing complete reliance on core rotation. Excellent for isolating the obliques and teaching pure torso rotation without hip involvement.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CHOP', 'KNEELING_ROTATIONAL_REVERSE_CHOP', 'STRENGTH', '{core,obliques,rotation,stability}', '{cable machine}', 'A reverse chop (lift) from tall kneeling position. Rotate from low to high while kneeling on both knees. This variation completely isolates the core rotational muscles by eliminating leg drive. Excellent for developing pure rotational strength through the torso.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CHOP', 'KNEELING_STABILITY_CHOP', 'STRENGTH', '{core,obliques,anti-rotation,stability}', '{cable machine}', 'An anti-rotation focused chop from tall kneeling. The emphasis is on maintaining stability and controlling the movement rather than generating explosive rotation. Builds deep core strength and teaches proper bracing patterns.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CHOP', 'KNEELING_WOODCHOPPER', 'STRENGTH', '{core,obliques,rotation}', '{cable machine}', 'The classic woodchop pattern performed from a kneeling position. This eliminates lower body involvement and isolates the core rotation. Excellent for developing rotational core strength and learning proper sequencing without compensation from the hips and legs.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CHOP', 'MEDICINE_BALL_WOOD_CHOPS', 'STRENGTH', '{core,obliques,rotation,power,full body}', '{medicine ball}', 'An explosive woodchop performed with a medicine ball. The ball can be slammed to the ground for maximum power output, or performed in a controlled manner for strength. This full-body exercise develops rotational power and teaches proper force transfer through the kinetic chain.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CHOP', 'POWER_SQUAT_CHOPS', 'STRENGTH', '{core,obliques,legs,quads,glutes,rotation,squat pattern,power}', '{medicine ball}', 'A dynamic combination of a squat and rotational chop. Squat down, then explosively stand while performing a chopping motion. This power exercise combines lower body drive with rotational core strength, developing total-body power and coordination.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CHOP', 'STANDING_ROTATIONAL_CHOP', 'STRENGTH', '{core,obliques,rotation,full body}', '{cable machine}', 'A woodchop performed from a standing position, allowing full-body rotation and hip involvement. This functional variation teaches proper rotational mechanics through the entire kinetic chain. The standing position allows for maximum power generation and sport-specific transfer.');

