INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BATTLE_ROPE', 'SIDEWINDER', 'STRENGTH', '{shoulders,arms,core,obliques,rotation}', '{battle rope}', 'Both arms move together side-to-side creating horizontal waves that snake along the ground. This lateral movement heavily emphasizes the obliques and rotational core strength while building shoulder endurance. The synchronized lateral motion creates unique coordination demands.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BATTLE_ROPE', 'SITTING_RUSSIAN_TWIST', 'STRENGTH', '{core,obliques,shoulders,arms,rotation}', '{battle rope}', 'Performed seated with legs elevated, rotating the torso side-to-side while swinging the ropes. This variation isolates the core, particularly the obliques, while the rope movement adds resistance and cardiovascular demand. Excellent for rotational core strength and endurance.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BATTLE_ROPE', 'SNAKE_WAVE', 'STRENGTH', '{shoulders,arms,core,rotation}', '{battle rope}', 'Creating horizontal waves that snake along the ground, often performed with a single arm or alternating arms in a fluid serpentine pattern. This movement challenges coordination and develops shoulder and grip endurance through a different plane of motion than vertical waves.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BATTLE_ROPE', 'SPLIT_JACK', 'STRENGTH', '{shoulders,arms,legs,cardio,full body}', '{battle rope}', 'Battle rope waves combined with split stance jumps (alternating forward and back leg positions). This plyometric variation builds lower body power and cardiovascular conditioning while challenging upper body endurance. The split jump adds an athletic, explosive element.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BATTLE_ROPE', 'STAGE_COACH', 'STRENGTH', '{shoulders,arms,core,legs}', '{battle rope}', 'A dynamic movement mimicking the reins of a stagecoach, with arms alternating in an up-and-down driving motion. This unique pattern challenges shoulder endurance from different angles while engaging the core for stability. The driving motion can be combined with squats or lunges for added difficulty.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BATTLE_ROPE', 'ULTIMATE_WARRIOR', 'STRENGTH', '{shoulders,arms,core,full body,power}', '{battle rope}', 'An intense combination movement incorporating multiple battle rope patterns in sequence or simultaneously. Typically combines slams, waves, and jumps in rapid succession. This advanced variation provides maximum metabolic demand and total-body conditioning.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BATTLE_ROPE', 'UPPER_CUTS', 'STRENGTH', '{shoulders,arms,core,power}', '{battle rope}', 'An explosive upward punching motion with the ropes, mimicking boxing uppercuts. Alternating arms drive upward forcefully, engaging the shoulders, core, and even the legs when performed with proper hip drive. Excellent for developing punching power and upper body explosiveness.');
