INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'ALTERNATING_DUMBBELL_CHEST_PRESS', 'STRENGTH', '{chest,triceps,shoulders,push,unilateral,stability}', '{dumbbells,bench}', 'A dumbbell bench press performed by alternating arms - pressing one dumbbell up while the other is held at chest level. This unilateral pattern increases core engagement and shoulder stability demands while addressing strength imbalances between sides. The constant tension on the non-pressing arm builds isometric endurance.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'ALTERNATING_DUMBBELL_CHEST_PRESS_ON_SWISS_BALL', 'STRENGTH', '{chest,triceps,shoulders,core,push,unilateral,stability}', '{dumbbells,swiss ball}', 'An alternating dumbbell press performed on a swiss ball instead of a bench. The unstable surface dramatically increases core and stabilizer activation while the alternating pattern challenges balance and anti-rotation strength. Excellent for building functional pressing strength and total-body stability.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'BARBELL_BENCH_PRESS', 'STRENGTH', '{chest,triceps,shoulders,push,compound}', '{barbell,bench}', 'The king of upper body exercises. Lying supine on a bench, lower the barbell to the chest and press it back up. This compound movement primarily targets the pectoralis major, anterior deltoids, and triceps. The barbell allows for maximum loading and is the standard for building upper body pressing strength and mass.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'BARBELL_BOARD_BENCH_PRESS', 'STRENGTH', '{chest,triceps,shoulders,push,power}', '{barbell,bench,boards}', 'A partial range bench press where boards (typically 1-5 inches thick) are placed on the chest to limit depth. This variation overloads the lockout portion, builds tricep strength, and allows for supra-maximal loading. Popular in powerlifting for building top-end strength and overcoming sticking points.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'BARBELL_FLOOR_PRESS', 'STRENGTH', '{chest,triceps,shoulders,push}', '{barbell}', 'A bench press variation performed lying on the floor instead of a bench. The floor limits range of motion, stopping the bar when elbows touch down. This reduces shoulder strain, emphasizes the lockout and triceps, and teaches leg drive without a bench. Excellent for shoulder health and tricep development.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'CLOSE_GRIP_BARBELL_BENCH_PRESS', 'STRENGTH', '{triceps,chest,shoulders,push}', '{barbell,bench}', 'A bench press performed with a narrower grip (hands roughly shoulder-width apart or slightly closer). This hand position shifts emphasis to the triceps while still engaging the chest. Essential for tricep mass and lockout strength. Keep elbows closer to the body to maximize tricep activation.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'DECLINE_DUMBBELL_BENCH_PRESS', 'STRENGTH', '{chest,lower chest,triceps,shoulders,push}', '{dumbbells,bench}', 'Dumbbell press performed on a decline bench (head lower than hips, typically 15-30 degrees). This angle emphasizes the lower and outer pectorals. The dumbbell variation allows for greater range of motion and independent arm movement compared to barbell. Excellent for complete chest development.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'DUMBBELL_BENCH_PRESS', 'STRENGTH', '{chest,triceps,shoulders,push,compound}', '{dumbbells,bench}', 'A bench press performed with dumbbells instead of a barbell. Each arm works independently, requiring greater stabilizer activation and preventing the stronger side from compensating. Allows for greater range of motion, more natural shoulder movement, and can be safer for those with shoulder issues. Excellent for balanced chest development.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'DUMBBELL_FLOOR_PRESS', 'STRENGTH', '{chest,triceps,shoulders,push}', '{dumbbells}', 'Dumbbell presses performed lying on the floor. The limited range of motion reduces shoulder stress while the independent dumbbell movement allows for natural arm paths. Excellent for building lockout strength, developing triceps, and providing a shoulder-friendly pressing variation.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'INCLINE_BARBELL_BENCH_PRESS', 'STRENGTH', '{chest,upper chest,shoulders,triceps,push}', '{barbell,bench}', 'Barbell bench press performed on an incline bench (typically 15-45 degrees). The incline shifts emphasis to the upper pectorals (clavicular head) and anterior deltoids. Essential for complete chest development and building shoulder strength. Lower inclines (15-30 degrees) tend to be most effective for upper chest.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'INCLINE_DUMBBELL_BENCH_PRESS', 'STRENGTH', '{chest,upper chest,shoulders,triceps,push}', '{dumbbells,bench}', 'Dumbbell press performed on an incline bench. Combines the upper chest emphasis of the incline with the independent arm movement and greater range of motion of dumbbells. Allows for more natural shoulder rotation and is often easier on the shoulders than the barbell variation.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'INCLINE_SMITH_MACHINE_BENCH_PRESS', 'STRENGTH', '{chest,upper chest,shoulders,triceps,push}', '{smith machine,bench}', 'Incline bench press performed on a Smith machine. The fixed bar path provides stability, allows for training to failure safely without a spotter, and can help isolate the chest by reducing stabilizer involvement. Good for targeting the upper chest with maximum load and controlled form.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'ISOMETRIC_BARBELL_BENCH_PRESS', 'STRENGTH', '{chest,triceps,shoulders,push,isometric}', '{barbell,bench}', 'A bench press variation where the bar is held statically at various positions (typically mid-range or just off the chest) for time. This isometric hold builds strength at specific angles, improves mind-muscle connection, and can help overcome sticking points. Also useful for rehab and teaching proper positioning.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'KETTLEBELL_CHEST_PRESS', 'STRENGTH', '{chest,triceps,shoulders,push,stability}', '{kettlebell,bench}', 'A chest press performed with kettlebells instead of dumbbells. The offset weight distribution of kettlebells increases stability demands and grip strength requirements. The unique loading creates different stimulus and challenges balance throughout the movement. Can be performed on bench or floor.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'NEUTRAL_GRIP_DUMBBELL_BENCH_PRESS', 'STRENGTH', '{chest,triceps,shoulders,push}', '{dumbbells,bench}', 'Dumbbell bench press performed with palms facing each other (neutral grip) throughout the movement. This grip is often more shoulder-friendly, reduces stress on the shoulder joint, and slightly increases tricep involvement. Excellent alternative for those with shoulder discomfort during traditional pressing.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'NEUTRAL_GRIP_DUMBBELL_INCLINE_BENCH_PRESS', 'STRENGTH', '{chest,upper chest,triceps,shoulders,push}', '{dumbbells,bench}', 'An incline dumbbell press using a neutral grip (palms facing each other). This combines the upper chest emphasis of the incline with the shoulder-friendly mechanics of the neutral grip. Often allows for more comfortable pressing and can be especially beneficial for those with shoulder issues.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'ONE_ARM_FLOOR_PRESS', 'STRENGTH', '{chest,triceps,shoulders,core,push,unilateral,stability}', '{dumbbells}', 'A floor press performed with one arm at a time. This unilateral variation significantly increases core and anti-rotation demands while addressing strength imbalances. The single-arm loading teaches proper bracing and builds unilateral pressing strength. Can be performed with dumbbell or kettlebell.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'PARTIAL_LOCKOUT', 'STRENGTH', '{triceps,chest,shoulders,push}', '{barbell,bench}', 'A partial range bench press focusing on the top portion of the movement (typically the final 6-12 inches). This variation overloads the triceps and lockout strength, allows for supra-maximal loading, and can help overcome sticking points. Often performed in a power rack with pins set at the desired height.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'REVERSE_GRIP_BARBELL_BENCH_PRESS', 'STRENGTH', '{chest,upper chest,triceps,biceps,shoulders,push}', '{barbell,bench}', 'Barbell bench press performed with an underhand (supinated) grip. This unique variation emphasizes the upper chest and reduces shoulder stress for some individuals. Also increases bicep activation. Requires careful spotting and controlled movement due to the unconventional grip position.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'REVERSE_GRIP_INCLINE_BENCH_PRESS', 'STRENGTH', '{chest,upper chest,triceps,biceps,shoulders,push}', '{barbell,bench}', 'An incline bench press performed with a reverse (underhand) grip. This combines the upper chest emphasis of the incline with the unique biomechanics of the reverse grip. Very challenging and requires careful control. Best performed with moderate weights and a spotter.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'SINGLE_ARM_CABLE_CHEST_PRESS', 'STRENGTH', '{chest,triceps,shoulders,core,push,unilateral,stability}', '{cable machine}', 'A unilateral chest press using a cable machine. The constant tension from the cable, combined with single-arm loading, creates significant core stability demands. The cable allows for pressing from various angles and maintains tension throughout the entire range. Excellent for functional strength and addressing imbalances.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'SINGLE_ARM_DUMBBELL_BENCH_PRESS', 'STRENGTH', '{chest,triceps,shoulders,core,push,unilateral,stability}', '{dumbbells,bench}', 'A bench press performed with one dumbbell at a time. The non-pressing arm can rest or extend out for balance. This unilateral variation significantly increases core anti-rotation demands while allowing full focus on each side independently. Excellent for identifying and correcting strength imbalances.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'SMITH_MACHINE_BENCH_PRESS', 'STRENGTH', '{chest,triceps,shoulders,push}', '{smith machine,bench}', 'Bench press performed on a Smith machine with its fixed bar path. The guided movement allows for training to failure safely, isolates the chest by reducing stabilizer involvement, and enables focus on pure strength. Can be performed at various angles to target different chest areas.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'SWISS_BALL_DUMBBELL_CHEST_PRESS', 'STRENGTH', '{chest,triceps,shoulders,core,push,stability}', '{dumbbells,swiss ball}', 'Dumbbell chest press performed while lying on a swiss ball instead of a bench. The unstable surface dramatically increases core activation and stabilizer engagement. Challenges balance and coordination while building functional pressing strength. The instability limits how much weight can be used but increases total-body engagement.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'TRIPLE_STOP_BARBELL_BENCH_PRESS', 'STRENGTH', '{chest,triceps,shoulders,push}', '{barbell,bench}', 'A bench press with three distinct pauses during the eccentric (lowering) and/or concentric (pressing) phases. These stops eliminate momentum, build strength at multiple joint angles, increase time under tension, and teach body awareness. Excellent for building control and addressing weak points.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'WEIGHTED_ONE_ARM_FLOOR_PRESS', 'STRENGTH', '{chest,triceps,shoulders,core,push,unilateral,stability}', '{dumbbells}', 'A single-arm floor press performed with a dumbbell or kettlebell. The floor limits range of motion while the unilateral loading creates anti-rotation core challenges. Excellent for building pressing strength, core stability, and addressing imbalances while being shoulder-friendly due to the limited range.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('BENCH_PRESS', 'WIDE_GRIP_BARBELL_BENCH_PRESS', 'STRENGTH', '{chest,outer chest,shoulders,triceps,push}', '{barbell,bench}', 'Bench press performed with a grip wider than standard (typically 1.5-2x shoulder width). This wider grip emphasizes the outer chest and reduces range of motion, allowing for heavier loads. However, it may increase shoulder strain for some individuals. Popular in powerlifting for maximizing leverages.');
