INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CARDIO', 'WEIGHTED_DOUBLE_UNDER', 'STRENGTH', '{cardio,calves,coordination,power}', '{weighted jump rope}', 'Double unders performed with a weighted jump rope (handles contain added weight). The heavier rope increases shoulder and grip demands while the movement pattern still requires explosive leg power. Builds upper body endurance alongside cardiovascular fitness and coordination.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CARDIO', 'WEIGHTED_JUMP_ROPE', 'STRENGTH', '{cardio,calves,shoulders,grip,endurance}', '{weighted jump rope}', 'Standard jump rope performed with a weighted rope. The added weight in the handles or rope increases shoulder, forearm, and grip demands while maintaining the cardiovascular and calf-building benefits. Excellent for building total-body endurance and grip strength.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CARDIO', 'WEIGHTED_JUMP_ROPE_CROSSOVER', 'STRENGTH', '{cardio,calves,coordination,shoulders,grip}', '{weighted jump rope}', 'Crossover jump rope performed with a weighted rope. This combines the coordination challenge of crossovers with increased shoulder and grip demands from the weighted rope. An advanced variation that builds upper body endurance, coordination, and cardiovascular fitness.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CARDIO', 'WEIGHTED_JUMP_ROPE_JOG', 'STRENGTH', '{cardio,calves,shoulders,endurance}', '{weighted jump rope}', 'Alternating foot jump rope (jogging style) with a weighted rope. The weighted rope increases upper body work while the jogging pattern allows for sustainable longer-duration efforts. Good for building cardiovascular endurance with added shoulder and grip strengthening.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CARDIO', 'WEIGHTED_JUMPING_JACKS', 'STRENGTH', '{cardio,full body,legs,shoulders}', '{dumbbells}', 'Jumping jacks performed while holding light dumbbells. The added weight increases shoulder and arm demands while maintaining the cardiovascular benefits. The weight also provides resistance during the overhead arm movement, building shoulder endurance alongside cardio fitness.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
