INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CARRY', 'FARMERS_CARRY_ON_TOES', 'STRENGTH', '{grip,forearms,core,traps,calves,carry,balance}', '{dumbbells}', 'A farmer''s carry variation performed while walking on the toes (plantar flexed ankles). This adds a significant calf and ankle stability challenge while maintaining all the grip and core benefits of standard farmer''s carries. Excellent for developing calf endurance and ankle stability under load.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CARRY', 'FARMERS_CARRY_WALK_LUNGE', 'STRENGTH', '{grip,forearms,core,legs,quads,glutes,lunge pattern,carry}', '{dumbbells}', 'A combination of farmer''s carry and walking lunges. Hold heavy weights at the sides while performing walking lunges. This brutal variation combines the grip and core demands of carries with the leg-building benefits of lunges. Excellent for total-body strength and conditioning.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CARRY', 'FARMERS_WALK', 'STRENGTH', '{grip,forearms,core,traps,full body,carry}', '{dumbbells}', 'Another name for farmer''s carry. Walking while holding heavy weights at the sides. This exercise builds functional, real-world strength including grip endurance, core stability, trap development, and mental toughness. A staple in strongman training and excellent for overall strength development.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CARRY', 'FARMERS_WALK_ON_TOES', 'STRENGTH', '{grip,forearms,core,traps,calves,carry,balance}', '{dumbbells}', 'Farmer''s walk performed while staying up on the toes throughout the movement. This variation significantly increases calf engagement and challenges balance while maintaining the grip and core benefits. The elevated position also increases time under tension for the calves.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CARRY', 'HEX_DUMBBELL_HOLD', 'STRENGTH', '{grip,forearms,shoulders,core,carry,isometric}', '{dumbbells}', 'An isometric hold where a hexagonal dumbbell is gripped by the weighted ends (not the handle) and held in various positions - typically in front of the body, overhead, or at the sides. This pinch-grip variation maximally challenges thumb and finger strength. Excellent for building crushing grip strength.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
