INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('ELLIPTICAL', 'ELLIPTICAL', 'STRENGTH', '{cardio,legs,low impact,endurance}', '{elliptical}', 'A low-impact cardiovascular exercise performed on an elliptical machine. The elliptical motion mimics running while eliminating the impact forces, making it joint-friendly for knees, hips, and ankles. Engages both upper and lower body when using the arm handles. Excellent for cardiovascular conditioning, endurance building, and active recovery.');
