INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('FLOOR_CLIMB', 'FLOOR_CLIMB', 'STRENGTH', '{full body,core,shoulders,cardio,functional}', '{bodyweight}', 'A dynamic movement transitioning from lying prone on the floor to standing, typically performed as quickly as possible. This full-body exercise combines core strength, shoulder stability, hip mobility, and cardiovascular conditioning. Excellent for developing functional fitness, agility, and the ability to get up from the ground efficiently.');
