INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_RAISE', 'BARBELL_HIP_THRUST_ON_FLOOR', 'STRENGTH', '{glutes,hips,hamstrings}', '{barbell}', 'A hip thrust performed lying on the floor with a barbell across the hips. Lie on your back, bend knees with feet flat, then drive through heels to lift hips and barbell up. This floor version has less range of motion than the bench version but still effectively targets the glutes. The barbell allows for heavy loading and progressive overload. Excellent glute builder.');


INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_RAISE', 'BARBELL_HIP_THRUST_WITH_BENCH', 'STRENGTH', '{glutes,hips,hamstrings}', '{barbell,bench}', 'The king of glute exercises - a barbell hip thrust with upper back supported on a bench. The elevated position allows for greater range of motion than floor thrusts. Drive through heels to thrust hips up until body forms a straight line. This exercise maximally activates the glutes and allows for very heavy loading. Essential for glute development and hip extension strength.');


INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_RAISE', 'BENT_KNEE_SWISS_BALL_REVERSE_HIP_RAISE', 'STRENGTH', '{glutes,hips,hamstrings,core}', '{swiss ball}', 'A reverse hip raise with feet on a swiss ball and knees bent. Lie face down on the ground, place feet on the ball, then pull the ball toward you by bending knees and lifting hips. The instability of the ball increases the challenge and engages the core. This variation builds glutes, hamstrings, and core stability simultaneously.');


INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_RAISE', 'BRIDGE_WITH_LEG_EXTENSION', 'STRENGTH', '{glutes,hips,hamstrings,core}', '{bodyweight}', 'A glute bridge with one leg extended straight out. Perform a standard bridge, then extend one leg straight while holding the bridge position. This variation increases the challenge on the working glute and adds core stability demands. Excellent for unilateral glute strength and identifying imbalances. Alternate legs for balanced development.');


INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_RAISE', 'CLAM_BRIDGE', 'STRENGTH', '{glutes,hips,hip abductors,core}', '{bodyweight}', 'A combination of a glute bridge and clamshell movement. Hold a bridge position while opening and closing the knees (clamshell motion). This exercise targets the glutes, particularly the gluteus medius, along with hip abductors. The combination builds glute strength in both hip extension and abduction. Excellent for hip stability and glute activation.');


INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_RAISE', 'FRONT_KICK_TABLETOP', 'STRENGTH', '{glutes,hips,core,hip flexors}', '{bodyweight}', 'A dynamic exercise from a tabletop position (hands and knees) where you kick one leg forward. From tabletop, extend one leg forward in a kicking motion while maintaining hip height. This movement combines hip extension with dynamic hip flexion. Builds glute strength, hip mobility, and core stability. Popular in Pilates and functional training.');


INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_RAISE', 'HIP_EXTENSION_AND_CROSS', 'STRENGTH', '{glutes,hips,core,obliques}', '{bodyweight}', 'A hip extension movement with a crossing pattern. From a quadruped position, extend one leg back and across the body. This variation adds rotation and targets the glutes while engaging the obliques and anti-rotation core muscles. The crossing pattern creates unique glute activation. Excellent for functional glute strength and core stability.');


INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_RAISE', 'HIP_RAISE', 'STRENGTH', '{glutes,hips,hamstrings}', '{bodyweight}', 'The fundamental glute bridge - lie on your back with knees bent, feet flat, then drive through heels to lift hips toward the ceiling. This basic but highly effective exercise targets the glutes and hamstrings while being accessible to all fitness levels. Essential movement pattern for posterior chain development, injury prevention, and improving hip extension strength. Foundation for all hip thrust variations.');


INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_RAISE', 'HIP_RAISE_WITH_FEET_ON_SWISS_BALL', 'STRENGTH', '{glutes,hips,hamstrings,core}', '{swiss ball}', 'A glute bridge with feet elevated on a swiss ball. The instability of the ball increases core engagement and balance demands while targeting the glutes and hamstrings. The elevation also provides greater range of motion. This variation is more challenging than floor bridges and builds stability alongside glute strength.');


INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_RAISE', 'HIP_RAISE_WITH_HEAD_ON_BOSU_BALL', 'STRENGTH', '{glutes,hips,hamstrings,core}', '{bosu ball}', 'A hip raise with upper back/shoulders resting on a BOSU ball. The unstable surface challenges balance and core stability while performing the bridge. This variation increases the difficulty and engages stabilizer muscles throughout the core and hips. Good for developing glute strength with added stability demands.');


INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_RAISE', 'HIP_RAISE_WITH_HEAD_ON_SWISS_BALL', 'STRENGTH', '{glutes,hips,hamstrings,core}', '{swiss ball}', 'A hip raise with upper back supported on a swiss ball instead of a bench. The ball creates instability, forcing greater core and stabilizer engagement. Drive through heels to lift hips while maintaining balance on the ball. This variation combines glute strength with significant balance and core stability demands.');


INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_RAISE', 'HIP_RAISE_WITH_KNEE_SQUEEZE', 'STRENGTH', '{glutes,hips,hamstrings,adductors}', '{bodyweight}', 'A glute bridge performed while squeezing the knees together or holding an object between the knees. The squeeze adds adductor (inner thigh) activation to the glute work. This variation teaches proper knee alignment during hip extension and builds both glutes and inner thighs. Can use a yoga block, foam roller, or mini ball between knees.');


INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_RAISE', 'INCLINE_REAR_LEG_EXTENSION', 'STRENGTH', '{glutes,hips,hamstrings}', '{bench}', 'A rear leg extension performed with hands on an elevated surface like a bench. From an incline plank position, extend one leg back and up. The incline angle changes the resistance curve and reduces the challenge compared to floor variations. Good for beginners building glute strength or for higher-rep glute activation work.');


INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_RAISE', 'KETTLEBELL_SWING', 'STRENGTH', '{glutes,hips,hamstrings,core,power,full body}', '{kettlebell}', 'A dynamic hip hinge movement swinging a kettlebell from between the legs to chest/eye level. The explosive hip drive from the glutes and hamstrings propels the bell forward. This power exercise builds explosive hip extension, cardiovascular endurance, and posterior chain strength. One of the most effective full-body exercises. Focus on hip drive, not arm lift.');


INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_RAISE', 'MARCHING_HIP_RAISE', 'STRENGTH', '{glutes,hips,hamstrings,core}', '{bodyweight}', 'A glute bridge held at the top while alternately lifting each foot off the ground in a marching motion. Hold the bridge position and march feet up and down. This variation adds core stability and unilateral challenge to the standard bridge. The marching requires anti-rotation core strength and builds glute endurance. Excellent for glute activation and stability.');


INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_RAISE', 'MARCHING_HIP_RAISE_WITH_FEET_ON_A_SWISS_BALL', 'STRENGTH', '{glutes,hips,hamstrings,core}', '{swiss ball}', 'A marching hip raise with feet elevated on a swiss ball. The combination of instability and marching motion creates an extremely challenging exercise for glutes, hamstrings, and core stability. Requires significant balance and core control. Advanced variation that builds glute strength and total body stability simultaneously.');


INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_RAISE', 'REVERSE_HIP_RAISE', 'STRENGTH', '{glutes,hips,hamstrings,lower back}', '{bodyweight}', 'A reverse hip raise performed lying face down, lifting the legs and hips off the ground. Lie prone on a bench or elevated surface with hips at the edge, then lift legs by extending the hips. This movement targets the glutes, hamstrings, and lower back extensors. Unlike standard bridges, the reverse variation emphasizes the posterior chain from a prone position. Excellent for glute and lower back development.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_RAISE', 'SINGLE_LEG_HIP_RAISE', 'STRENGTH', '{glutes,hips,hamstrings,core,unilateral}', '{bodyweight}', 'A single-leg glute bridge - perform a bridge while keeping one leg extended straight. This unilateral variation significantly increases the challenge on the working glute and demands greater core stability to prevent rotation. Excellent for identifying and correcting strength imbalances between sides. More challenging than the bilateral version due to increased load and stability demands. Essential for single-leg strength and glute development.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_RAISE', 'SINGLE_LEG_HIP_RAISE_WITH_FOOT_ON_BENCH', 'STRENGTH', '{glutes,hips,hamstrings,core,unilateral}', '{bench}', 'A single-leg hip raise with the working foot elevated on a bench. The elevation increases range of motion and challenges the glute through a longer movement path. This unilateral variation combines the benefits of single-leg training with increased difficulty from the elevated position. Builds exceptional glute strength and hip stability. Progress to this after mastering floor single-leg bridges.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HIP_RAISE', 'SINGLE_LEG_HIP_RAISE_WITH_FOOT_ON_BOSU_BALL', 'STRENGTH', '{glutes,hips,hamstrings,core,unilateral,stability}', '{bosu ball}', 'A single-leg hip raise with the working foot on a BOSU ball. The unstable surface significantly increases balance and stability demands while targeting the glute. This advanced variation requires exceptional core control and ankle/foot stability. Builds glute strength alongside functional balance and proprioception. Only attempt after mastering stable surface single-leg bridges.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
