INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('HYPEREXTENSION', 'HOLLOW_HOLD_AND_ROLL', 'STRENGTH', '{core,abs,lower back,isometric}', '{bodyweight}', 'Hold a hollow body position (lying on back, arms and legs extended off ground) then roll side to side. This combines isometric core hold with dynamic rolling movement. Builds exceptional core strength, body awareness, and control. The hollow hold is fundamental for gymnastics and advanced core training. Challenges the entire anterior core chain.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
