INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('LEG_CURL', 'LEG_CURL', 'STRENGTH', '{hamstrings,knee flexion,isolation}', '{leg curl machine}', 'The classic leg curl machine exercise - THE isolation movement for hamstrings. Lie face down (or sit), curl weight pad toward glutes by bending knees. This isolation exercise directly targets the hamstring muscles through knee flexion. Essential for hamstring development, knee health, and balanced leg strength. Can be performed lying, seated, or standing. One of the most effective exercises for direct hamstring work.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
