INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('LEG_CURL', 'SINGLE_LEG_BARBELL_GOOD_MORNING', 'STRENGTH', '{hamstrings,glutes,lower back,hinge pattern,unilateral,balance}', '{barbell}', 'Single-leg good morning with barbell - an advanced unilateral posterior chain exercise. Bar on shoulders, stand on one leg, hinge forward while maintaining balance. This challenging variation builds tremendous single-leg strength, balance, and stability. Addresses left-right imbalances in posterior chain development. Requires significant core strength and proprioception. Excellent for athletic performance and injury prevention.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
