INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('LEG_RAISE', 'LATERAL_STEPOVER', 'STRENGTH', '{hips,hip mobility,legs,core}', '{bodyweight}', 'Step laterally over an object or imaginary barrier, lifting leg high and across the body. This dynamic movement builds hip mobility, lateral leg strength, and coordination. The stepping pattern engages hip abductors and core stabilizers. Excellent for athletic warm-ups, agility training, and improving hip mobility. Common in sports performance training. Can be performed over hurdles or cones for added challenge.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('LEG_RAISE', 'LEG_LOWERING_DRILL', 'STRENGTH', '{core,abs,hip flexors,lower back}', '{bodyweight}', 'Lie on back, raise legs to vertical, slowly lower them toward floor without letting lower back arch. This controlled eccentric exercise builds exceptional core strength and teaches proper spinal stabilization. The key is maintaining lower back contact with floor throughout. Stop lowering before back arches. Excellent for developing core control and preventing lower back pain. Used extensively in physical therapy and core training.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('LEG_RAISE', 'LYING_STRAIGHT_LEG_RAISE', 'STRENGTH', '{core,abs,hip flexors}', '{bodyweight}', 'Classic floor leg raise - lie on back, raise straight legs from floor to vertical. This fundamental core exercise targets the lower abs and hip flexors. Essential for core development and ab definition. Keep lower back pressed to floor throughout. More challenging than bent-knee variations. Can be made easier by placing hands under glutes. Foundational exercise for core strength.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('LEG_RAISE', 'MEDICINE_BALL_LEG_DROPS', 'STRENGTH', '{core,abs,hip flexors,adductors}', '{medicine ball}', 'Leg lowering drill with medicine ball gripped between feet. Lie on back, grip medicine ball between ankles, raise to vertical, slowly lower with control. The medicine ball adds resistance and requires adductor engagement. This weighted variation significantly increases difficulty of the leg lowering drill. Builds exceptional core strength and control. The unstable ball challenges stabilization throughout the movement.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('LEG_RAISE', 'QUADRUPED_LEG_RAISE', 'STRENGTH', '{glutes,hamstrings,core,balance}', '{bodyweight}', 'On hands and knees, extend one leg straight back and up. This fundamental exercise targets the glutes and hamstrings while requiring core stabilization. The quadruped position challenges balance and anti-rotation control. Excellent for glute activation, hip extension strength, and core stability. Common in physical therapy and corrective exercise. Can be combined with arm raises for added difficulty (bird dog).');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('LEG_RAISE', 'REVERSE_LEG_RAISE', 'STRENGTH', '{glutes,hamstrings,lower back}', '{bodyweight}', 'Lie face down and raise both legs off the floor. This posterior chain exercise targets the glutes, hamstrings, and lower back. The prone position emphasizes hip extension and posterior muscles. Excellent for building lower back strength, improving posture, and activating the glutes. Can be held isometrically or performed dynamically. Good complement to traditional ab exercises for balanced core development.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('LEG_RAISE', 'REVERSE_LEG_RAISE_ON_SWISS_BALL', 'STRENGTH', '{glutes,hamstrings,lower back,core,stability}', '{swiss ball}', 'Reverse leg raise performed while lying face down on a swiss ball. The unstable ball surface dramatically increases core stability demands compared to floor version. This variation requires constant balance and stabilization while targeting the posterior chain. Excellent for building functional strength, improving balance, and developing total body control. The instability engages deep core stabilizers.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('LEG_RAISE', 'SINGLE_LEG_LOWERING_DRILL', 'STRENGTH', '{core,abs,hip flexors,obliques,unilateral}', '{bodyweight}', 'Single-leg variation of leg lowering drill - raise one leg to vertical, slowly lower with control while keeping lower back flat. This unilateral version adds anti-rotation demands and identifies imbalances between sides. More challenging than bilateral version due to asymmetrical loading. Excellent for core stability, addressing imbalances, and building single-leg control. Keep non-working leg bent with foot on floor.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('LEG_RAISE', 'WEIGHTED_HANGING_KNEE_RAISE', 'STRENGTH', '{core,abs,hip flexors,grip}', '{pull-up bar,ankle weights,dumbbell}', 'Hanging knee raises with added weight via ankle weights or dumbbell gripped between feet. The additional resistance significantly increases core and hip flexor demands. This progression builds exceptional core strength and control. The weight makes each rep more challenging and builds more strength. Advanced variation for serious core development. Progress weight gradually.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('LEG_RAISE', 'WEIGHTED_HANGING_LEG_RAISE', 'STRENGTH', '{core,abs,hip flexors,grip}', '{pull-up bar,ankle weights,dumbbell}', 'The ultimate hanging ab exercise - straight-leg hanging raise with added weight. This brutally difficult movement builds elite-level core strength and control. The combination of straight legs plus weight creates maximum resistance. Only for advanced athletes who have mastered unweighted hanging leg raises. Builds exceptional abdominal strength and muscular development.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('LEG_RAISE', 'WEIGHTED_HANGING_SINGLE_LEG_RAISE', 'STRENGTH', '{core,abs,hip flexors,obliques,unilateral,grip}', '{pull-up bar,ankle weights}', 'Single-leg hanging raise with ankle weights - an extremely challenging unilateral core exercise. The combination of single-leg, straight-leg, weighted, and hanging creates maximum difficulty. This elite-level exercise builds exceptional core strength and addresses imbalances. The asymmetrical load demands tremendous anti-rotation control. Only for very advanced athletes.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('LEG_RAISE', 'WEIGHTED_LATERAL_STEPOVER', 'STRENGTH', '{hips,hip mobility,legs,core}', '{ankle weights,weight vest,dumbbells}', 'Lateral stepover with added weight via ankle weights, weight vest, or dumbbells. The additional resistance increases strength demands while maintaining the mobility and coordination benefits. This weighted variation builds hip strength and power for lateral movements. Excellent for athletic training and improving weighted hip mobility. Progress weight gradually to maintain proper form.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('LEG_RAISE', 'WEIGHTED_LEG_LOWERING_DRILL', 'STRENGTH', '{core,abs,hip flexors,lower back}', '{ankle weights}', 'Leg lowering drill with ankle weights added. The additional resistance makes maintaining lower back position significantly more challenging. This advanced progression builds exceptional core control and strength. The weight increases the leverage against the core throughout the eccentric lowering. Only for those who have mastered bodyweight version with perfect form. Builds elite core strength.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('LEG_RAISE', 'WEIGHTED_LYING_STRAIGHT_LEG_RAISE', 'STRENGTH', '{core,abs,hip flexors}', '{ankle weights}', 'Floor leg raises with ankle weights added for increased resistance. The additional weight creates greater strength demands on the abs and hip flexors. This progression builds exceptional core strength beyond bodyweight training. Maintain lower back contact with floor throughout. Advanced variation for those who can easily perform high-rep bodyweight leg raises. Builds abdominal strength and definition.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
