INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('LEG_RAISE', 'WEIGHTED_REVERSE_LEG_RAISE_ON_SWISS_BALL', 'STRENGTH', '{glutes,hamstrings,lower back,core,stability}', '{swiss ball,ankle weights}', 'Weighted reverse leg raise on swiss ball - combining instability and resistance. Lie face down on ball with ankle weights, raise legs. This extremely challenging variation demands exceptional balance, core stability, and posterior chain strength simultaneously. The combination of ball instability and added weight creates maximum difficulty. Elite-level exercise for advanced athletes.');

