INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('LUNGE', 'ALTERNATING_BARBELL_FORWARD_LUNGE', 'STRENGTH', '{legs,quads,glutes,lunge pattern,unilateral}', '{barbell}', 'Forward lunges with barbell on back, alternating legs each rep. Step forward, lower until rear knee nearly touches ground, push back to start. This classic loaded lunge builds exceptional leg strength, balance, and coordination. The alternating pattern allows brief rest between legs. Targets quads, glutes, and hamstrings. Essential exercise for building functional lower body strength.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('LUNGE', 'ALTERNATING_DUMBBELL_LUNGE', 'STRENGTH', '{legs,quads,glutes,lunge pattern,unilateral}', '{dumbbells}', 'Forward lunges holding dumbbells at sides, alternating legs. The dumbbells allow for natural arm position and easier balance than barbell. This fundamental loaded lunge variation builds leg strength, stability, and muscular endurance. Excellent for all training levels. The alternating pattern develops coordination and addresses imbalances. One of the best lower body exercises.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
