INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('LUNGE', 'BACK_FOOT_ELEVATED_DUMBBELL_SPLIT_SQUAT', 'STRENGTH', '{legs,quads,glutes,squat pattern,lunge pattern,unilateral}', '{dumbbells,bench}', 'Bulgarian split squat with dumbbells - rear foot elevated on bench. This exceptional single-leg exercise builds massive quad and glute strength. The elevated rear foot increases range of motion and front leg loading. One of the best exercises for single-leg strength and muscle development. Addresses imbalances and builds functional strength. Also called Bulgarian split squat.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('LUNGE', 'BARBELL_BOX_LUNGE', 'STRENGTH', '{legs,quads,glutes,lunge pattern,unilateral}', '{barbell,box}', 'Lunge forward onto an elevated box or platform with barbell. The elevation increases range of motion and quad/glute engagement. This variation builds exceptional leg strength and power. The step-up component adds an explosive element. Excellent for athletic performance and lower body development. Can be performed as step-up lunge or standard lunge onto box.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
