INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('LUNGE', 'BARBELL_CROSSOVER_LUNGE', 'STRENGTH', '{legs,quads,glutes,adductors,lunge pattern,unilateral}', '{barbell}', 'Crossover or curtsy lunge with barbell - step one leg behind and across the body. This multi-planar movement targets glutes, especially gluteus medius, plus adductors. The crossing pattern challenges balance and stability. Excellent for building hip strength and addressing imbalances. The lateral component engages muscles missed by forward/reverse lunges.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('LUNGE', 'BARBELL_FRONT_SPLIT_SQUAT', 'STRENGTH', '{legs,quads,glutes,squat pattern,lunge pattern,unilateral}', '{barbell}', 'Split squat with barbell in front rack position. The front-loaded position increases quad emphasis and requires more upright torso. This variation is more knee-dominant than back squat position. Builds exceptional quad strength while being easier on the lower back. The front load challenges core stability. Excellent for Olympic weightlifters and those with back issues.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('LUNGE', 'BARBELL_LUNGE', 'STRENGTH', '{legs,quads,glutes,lunge pattern,unilateral}', '{barbell}', 'The classic barbell lunge - barbell on back, step forward into lunge. This fundamental loaded lunge variation builds total leg strength and size. Can be performed in place, alternating, or walking. Essential compound exercise for leg development. The barbell allows for heavy progressive overload. One of the best exercises for building functional lower body strength.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
