INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('OLYMPIC_LIFT', 'CLEAN', 'STRENGTH', '{legs,back,shoulders,olympic lift,power,explosive,triple extension}', '{barbell}', 'Generic clean movement - explosive pull from floor to shoulders. This fundamental Olympic lift variation builds total body power and athleticism. The clean movement pattern is essential for power development. Can refer to power clean or squat clean. Widely used across all strength and conditioning programs. Builds explosive strength and coordination.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('OLYMPIC_LIFT', 'CLEAN_AND_JERK', 'STRENGTH', '{legs,back,shoulders,olympic lift,power,explosive,triple extension}', '{barbell}', 'THE Olympic lift - clean barbell to shoulders, then jerk overhead. One of two Olympic competition lifts. This complete movement combines pulling power, squatting strength, and overhead pressing power. The ultimate test of total body strength and power. Essential for Olympic weightlifting. Builds exceptional athleticism and full-body power. The most complete strength exercise.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('OLYMPIC_LIFT', 'CLEAN_AND_PRESS', 'STRENGTH', '{legs,back,shoulders,push,olympic lift,power,explosive}', '{barbell}', 'Clean to shoulders, then strict press overhead (no leg drive). This old-school Olympic lift was removed from competition in 1972. The strict press component builds pure shoulder strength. Combines explosive clean with strength press. Excellent for building total body strength and pressing power. Still widely used in strength training despite removal from Olympic competition.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
