INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('OLYMPIC_LIFT', 'DUMBBELL_POWER_CLEAN_AND_JERK', 'STRENGTH', '{legs,back,shoulders,olympic lift,power,explosive,triple extension}', '{dumbbells}', 'Complete dumbbell Olympic lift - explosive clean to shoulders, then jerk overhead. The dumbbell version allows independent arm movement throughout both phases. This full movement combines pulling power, catching, and overhead pressing. Excellent for building total body power and coordination with more natural movement patterns than barbell. The dumbbells reduce wrist and shoulder stress. Great alternative to barbell clean and jerk.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
