INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('OLYMPIC_LIFT', 'DUMBBELL_SNATCH', 'STRENGTH', '{legs,back,shoulders,olympic lift,power,explosive,triple extension}', '{dumbbells}', 'Full dumbbell snatch from floor to overhead - explosive total body movement. Pull dumbbells from floor to overhead in one continuous motion. The dumbbell version allows for more natural movement patterns than barbell. This variation is easier on wrists and shoulders while still building explosive power. Excellent for developing unilateral power and coordination. Can be performed with one or two dumbbells. Great alternative to barbell snatch.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('OLYMPIC_LIFT', 'MEDICINE_BALL_CLEAN', 'STRENGTH', '{legs,back,shoulders,olympic lift,power,explosive,triple extension}', '{medicine ball}', 'Explosive clean movement using a medicine ball - pull from floor to shoulders. The medicine ball provides a unique gripping challenge and allows for safe learning of clean mechanics. This variation is excellent for beginners learning Olympic movements or for conditioning work. The lighter weight allows focus on speed and technique. Can be performed at high volume for power endurance. Great teaching tool for the clean movement pattern.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
