INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('PLANK', '_90_DEGREE_STATIC_HOLD', 'STRENGTH', '{core,abs,hip flexors,shoulders,isometric}', '{bodyweight}', 'Advanced static hold with body at 90-degree angle - typically L-sit or hanging L-hold position. Hold body with legs extended at 90 degrees from torso. This brutally challenging isometric exercise builds exceptional core strength, hip flexor endurance, and shoulder stability. The 90-degree position requires tremendous abdominal and hip flexor strength. Elite-level core exercise. Common in gymnastics and advanced calisthenics.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
