INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('PLANK', 'CROSS_BODY_MOUNTAIN_CLIMBER', 'STRENGTH', '{core,abs,obliques,conditioning,cardio}', '{bodyweight}', 'Mountain climber variation bringing knee toward opposite elbow - targeting obliques and rotation. In plank position, drive knee across body toward opposite elbow. This dynamic oblique exercise builds rotational core strength and cardiovascular endurance. The cross-body pattern engages obliques more than standard mountain climbers. Excellent for core conditioning and fat loss. Popular in HIIT workouts and circuit training.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('PLANK', 'ELBOW_PLANK_PIKE_JACKS', 'STRENGTH', '{core,abs,shoulders,hip flexors,conditioning}', '{bodyweight}', 'Dynamic plank variation combining pike position with jumping jacks motion. From elbow plank, jump feet apart while piking hips up, then jump back together. This challenging exercise combines core strength, shoulder stability, hip flexor engagement, and cardiovascular conditioning. The jumping motion adds plyometric element. Excellent for advanced core training and athletic conditioning. Builds explosive core power.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
