INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('PLANK', 'ELEVATOR_ABS', 'STRENGTH', '{core,abs,isometric}', '{bodyweight}', 'Plank variation with pulsing hip movement - lower hips slightly then elevate back to plank. The small pulsing movements maintain constant tension on the core. This dynamic plank challenges endurance and control. The micro-movements ("elevator" up and down) build exceptional core stability. Excellent for building time under tension and muscular endurance. Harder than static plank despite small range of motion.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
