INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('PULL_UP', '_30_DEGREE_LAT_PULLDOWN', 'STRENGTH', '{back,lats,pull}', '{cable machine}', 'Lat pulldown at 30-degree incline - angled pulling variation. Pull cable down at 30-degree angle from vertical. This angled pulldown variation changes the pulling angle and muscle recruitment pattern. The 30-degree position emphasizes different portions of the lats and back muscles. Excellent for complete lat development from multiple angles. The angle variation prevents adaptation plateaus. Builds back strength from varied pulling angles.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('PULL_UP', 'BAND_ASSISTED_CHIN_UP', 'STRENGTH', '{back,lats,biceps,pull}', '{resistance band,pull-up bar}', 'Band-assisted chin-up - THE best chin-up progression for beginners. Loop resistance band over bar, place knees or feet in band for assistance. This assisted variation allows learning proper chin-up mechanics while building strength. The band provides variable assistance (most at bottom, least at top). Essential progression toward unassisted chin-ups. Allows higher rep ranges for muscle building. Perfect for those working toward their first chin-up.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('PULL_UP', 'BAND_ASSISTED_PULL_UP', 'STRENGTH', '{back,lats,pull}', '{resistance band,pull-up bar}', 'Band-assisted pull-up - THE best pull-up progression tool. Loop band over bar, place knees or feet in band for assistance. This assisted variation makes pull-ups accessible while building strength toward unassisted reps. The band provides graduated assistance throughout the movement. Essential for learning proper pull-up form and building strength. More effective than assisted pull-up machines for carryover. The most popular pull-up progression method.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('PULL_UP', 'BANDED_PULL_UPS', 'STRENGTH', '{back,lats,pull}', '{resistance band,pull-up bar}', 'Pull-ups with resistance band added for extra resistance or assistance. Band can assist (looped for support) or resist (anchored below for added tension). This versatile variation allows for both regression and progression. Bands can add accommodating resistance at top of movement. Excellent for building explosive power or endurance. The variable resistance creates unique training stimulus.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
