INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'BICYCLE_CRUNCH', 'STRENGTH', '{core,abs,obliques,rotation}', '{bodyweight}', 'One of the most effective ab exercises according to research. Lying on your back, alternately bring opposite elbow to opposite knee in a pedaling motion while extending the other leg. This dynamic movement targets the rectus abdominis and obliques simultaneously through rotation and flexion. The alternating pattern creates constant tension and engages the entire core. Excellent for building functional core strength and definition.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'CABLE_CRUNCH', 'STRENGTH', '{core,abs,weighted}', '{cable machine}', 'A weighted crunch performed kneeling in front of a cable machine with a rope attachment. Pull the rope down while crunching forward, bringing your elbows toward your knees. The cable provides constant tension throughout the movement and allows for progressive overload. Excellent for building ab strength and size. Focus on spinal flexion rather than just pulling with the arms.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'CIRCULAR_ARM_CRUNCH', 'STRENGTH', '{core,abs,shoulders}', '{bodyweight}', 'A crunch variation where the arms move in circular motions while performing the crunch. As you crunch up, move your arms in circles around your body. This variation adds an unstable element and shoulder engagement to the traditional crunch. The arm movement increases the challenge and helps prevent momentum from dominating the movement.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'CROSS_LEG_REVERSE_CRUNCH', 'STRENGTH', '{core,abs,lower abs}', '{bodyweight}', 'A reverse crunch performed with legs crossed at the ankles. Lying on your back with legs crossed, curl your hips up toward your ribcage using your lower abs. The crossed leg position adds stability and allows focus on the lower abdominal region. This variation is slightly easier than standard reverse crunches, making it good for beginners.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'CROSSED_ARMS_CRUNCH', 'STRENGTH', '{core,abs}', '{bodyweight}', 'A standard crunch performed with arms crossed over the chest, hands on opposite shoulders. This arm position is slightly more challenging than hands behind head because it removes any possibility of pulling on the neck. The crossed arms position is ideal for maintaining proper form and isolating the abs. A classic ab exercise suitable for all fitness levels.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'CRUNCH', 'STRENGTH', '{core,abs}', '{bodyweight}', 'The foundational ab exercise. Lying on your back with knees bent, lift your shoulders off the ground by contracting your abs, focusing on spinal flexion. Unlike sit-ups, crunches involve only the upper portion of the movement, isolating the rectus abdominis. Keep your lower back on the floor and avoid pulling on your neck. Essential for core development and accessible to all fitness levels.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'CRUNCH_CHOP', 'STRENGTH', '{core,abs,obliques,rotation}', '{bodyweight}', 'A crunch combined with a chopping motion, bringing hands from one side overhead down toward the opposite hip as you crunch up. This dynamic movement adds rotation to the traditional crunch, engaging the obliques and creating a more functional pattern. The chopping motion mimics athletic movements and builds rotational core strength.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'DOUBLE_CRUNCH', 'STRENGTH', '{core,abs,lower abs}', '{bodyweight}', 'An advanced crunch variation that combines a standard crunch with a reverse crunch simultaneously. Lift your shoulders toward your knees while bringing your knees toward your shoulders, meeting in the middle. This exercise targets both upper and lower abs in one movement, creating intense abdominal contraction. Excellent for building overall core strength and definition.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'ELBOW_TO_KNEE_CRUNCH', 'STRENGTH', '{core,abs,obliques}', '{bodyweight}', 'A crunch where you bring your elbow to meet your knee, either on the same side or opposite sides. The opposite-side version (similar to bicycle crunches) adds rotation and engages obliques. This variation allows for various patterns and keeps constant tension on the core. Can be performed with both feet on ground or one leg extended.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'FLUTTER_KICKS', 'STRENGTH', '{core,abs,lower abs,hip flexors}', '{bodyweight}', 'A classic core exercise where you lie on your back and alternately kick your legs up and down in a fluttering motion. Keep your lower back pressed to the floor and legs relatively straight. This exercise heavily engages the lower abs and hip flexors while building endurance. Popular in military PT and swimming training for developing core stability and hip flexor strength.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'FOAM_ROLLER_REVERSE_CRUNCH_ON_BENCH', 'STRENGTH', '{core,abs,lower abs}', '{foam roller,bench}', 'A reverse crunch performed on a bench while squeezing a foam roller between your thighs or ankles. The foam roller adds an adductor activation component while performing the reverse crunch. The bench provides elevation for increased range of motion. This variation builds lower ab strength while engaging the inner thighs.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'FOAM_ROLLER_REVERSE_CRUNCH_WITH_DUMBBELL', 'STRENGTH', '{core,abs,lower abs,weighted}', '{dumbbells,foam roller}', 'A weighted reverse crunch holding a dumbbell between your feet or ankles, with a foam roller for added instability. The dumbbell provides resistance for progressive overload while the foam roller engages adductors. This advanced variation significantly increases the challenge compared to bodyweight reverse crunches. Excellent for building lower ab strength.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'FOAM_ROLLER_REVERSE_CRUNCH_WITH_MEDICINE_BALL', 'STRENGTH', '{core,abs,lower abs,weighted}', '{medicine ball,foam roller}', 'A reverse crunch performed while squeezing a medicine ball between your knees or ankles, with a foam roller for variation. The medicine ball provides resistance and requires adductor engagement to maintain position. This combination builds lower ab strength while developing inner thigh control. The larger surface area of the medicine ball makes it easier to grip than a dumbbell.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'FROG_PRESS', 'STRENGTH', '{core,abs,lower abs}', '{bodyweight}', 'A core exercise where you lie on your back with the soles of your feet together in a frog-leg position, then press your legs out and away while maintaining core tension. This movement challenges the lower abs and hip control in an extended position. The frog leg position opens the hips while emphasizing abdominal control.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'HANGING_KNEE_RAISE_OBLIQUE_CRUNCH', 'STRENGTH', '{core,abs,obliques,grip,lower abs}', '{pull-up bar}', 'A hanging knee raise with a twist - bring your knees toward one side while hanging from a bar. This exercise combines lower ab work with oblique engagement through rotation. The hanging position adds grip strength demands and decompresses the spine. Excellent for building functional core strength and grip endurance. More advanced than standard hanging knee raises.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'HIP_CROSSOVER', 'STRENGTH', '{glutes,hips,core,abs,obliques,rotation}', '{bodyweight}', 'A rotational core exercise where you lie on your back with arms extended, then drop bent knees from side to side across your body. This movement heavily engages the obliques and lower abs through rotation while stretching the hips and glutes. The crossover motion builds rotational control and spinal mobility. Also known as windshield wipers when performed with straight legs.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'HOLLOW_ROCK', 'STRENGTH', '{core,abs,lower abs,isometric}', '{bodyweight}', 'A gymnastics-based core exercise creating a hollow body position then rocking back and forth. Lie on your back with arms overhead and legs extended, press your lower back flat, then rock like a rocking chair. This isometric hold builds tremendous core strength and body awareness. The hollow position is fundamental to gymnastics and develops the ability to maintain spinal position under movement. Highly challenging.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'INCLINE_REVERSE_CRUNCH', 'STRENGTH', '{core,abs,lower abs}', '{bench}', 'A reverse crunch performed on an incline bench with your head higher than your hips. The incline increases the difficulty by requiring the abs to work against a greater gravitational challenge. Grip the bench behind your head and curl your hips toward your ribcage. This variation provides a more challenging progression than flat reverse crunches.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'KNEELING_CABLE_CRUNCH', 'STRENGTH', '{core,abs,weighted}', '{cable machine}', 'The most popular weighted ab exercise - kneeling in front of a cable machine with a rope attachment held behind your head. Crunch forward by flexing the spine, bringing your elbows toward your knees. The cable provides constant tension and allows for progressive overload. Focus on spinal flexion and avoid turning it into a hip hinge. Essential for building ab strength and mass.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'KNEELING_CROSS_CRUNCH', 'STRENGTH', '{core,abs,obliques,rotation}', '{bodyweight}', 'A kneeling crunch with rotation - bring opposite elbow toward opposite knee while in a kneeling position. This variation combines the stability challenge of kneeling with rotational core work. The kneeling position removes leg drive and forces the core to do all the work. Excellent for developing rotational strength and balance.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'KNEELING_OBLIQUE_CABLE_CRUNCH', 'STRENGTH', '{core,abs,obliques,weighted,rotation}', '{cable machine}', 'A cable crunch variation performed kneeling but with rotation toward one side. Pull the cable while crunching and rotating to target the obliques. This exercise combines the benefits of weighted resistance with rotational core training. Perform equal reps on both sides. Excellent for building oblique strength and definition with progressive resistance.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'KNEES_TO_ELBOW', 'STRENGTH', '{core,abs,obliques}', '{bodyweight}', 'A core exercise bringing your knees toward your elbows, either from a plank position or lying down. The movement can be performed with rotation (opposite knee to elbow) or straight (both knees to both elbows). This dynamic exercise builds core strength, hip flexor endurance, and coordination. Popular in CrossFit and functional fitness training.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'LEG_CLIMB_CRUNCH', 'STRENGTH', '{core,abs}', '{bodyweight}', 'A crunch where you "climb" up your leg with your hands as you crunch toward one extended leg. Lie on your back, extend one leg up, then crunch up while walking your hands up your shin/thigh. This variation increases range of motion and adds an isometric element as you hold positions along the leg. Good for building endurance and mind-muscle connection.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'LEG_EXTENSIONS', 'STRENGTH', '{core,abs,lower abs,hip flexors}', '{bodyweight}', 'A core exercise where you extend your legs out from a contracted position. Sitting or lying back, bring knees to chest then extend legs out while maintaining core engagement. This movement challenges the lower abs and hip flexors, especially in the extended position. The anti-extension component builds core stability and control.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'LEG_LEVERS', 'STRENGTH', '{core,abs,lower abs,hip flexors}', '{bodyweight}', 'An advanced core exercise lying on your back and lowering straight legs from vertical to just above the ground, then raising back up. This movement intensely challenges the lower abs and hip flexors with a long lever arm. Keep your lower back pressed to the floor throughout. Excellent for building lower ab strength but requires good core control to perform safely.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'MCGILL_CURL_UP', 'STRENGTH', '{core,abs}', '{bodyweight}', 'A spine-safe crunch developed by Dr. Stuart McGill for core endurance. Lie with one knee bent, one leg straight, hands under lower back for support. Lift shoulders slightly off the ground while maintaining neutral spine. The minimal range of motion and spinal support make this ideal for those with back issues. Focus on endurance over range. Part of the McGill Big 3 core stability program.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'MODIFIED_PILATES_ROLL_UP_WITH_BALL', 'STRENGTH', '{back,core,abs}', '{exercise ball}', 'A Pilates roll-up performed with an exercise ball held in the hands or between the legs. The ball adds resistance and helps guide the movement as you roll up from lying to sitting position. This modification makes the exercise slightly easier than the full roll-up while still building core strength and spinal articulation. Emphasizes controlled segmental spinal flexion.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'PILATES_CRUNCH', 'STRENGTH', '{back,core,abs}', '{bodyweight}', 'A controlled, precise crunch emphasizing Pilates principles of breath, control, and spinal articulation. Perform the crunch with slow, deliberate movement, focusing on peeling the spine off the mat vertebra by vertebra. Pilates crunches emphasize quality over quantity and proper breathing patterns. Excellent for developing mind-muscle connection and core control.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'PILATES_ROLL_UP_WITH_BALL', 'STRENGTH', '{back,core,abs}', '{exercise ball}', 'A full Pilates roll-up performed with an exercise ball for added challenge and guidance. Slowly roll from lying flat to sitting position, articulating through each vertebra, with the ball held overhead or between legs. The ball adds weight and helps maintain proper arm/leg position. This exercise builds tremendous core strength and spinal mobility through the full range of flexion.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'RAISED_LEGS_CRUNCH', 'STRENGTH', '{core,abs,lower abs}', '{bodyweight}', 'A crunch performed with legs raised at 90 degrees, either bent at the knees or straight up. The raised leg position increases the difficulty by engaging the lower abs to stabilize the legs while crunching. This variation removes any leg drive and isolates the abs more effectively than standard crunches. The higher the legs, the more challenging the exercise.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'REVERSE_CRUNCH', 'STRENGTH', '{core,abs,lower abs}', '{bodyweight}', 'A fundamental lower ab exercise where you curl your hips up toward your ribcage. Lying on your back with knees bent, lift your hips off the ground by contracting your lower abs. Unlike standard crunches that target upper abs, reverse crunches emphasize the lower abdominal region. Essential for complete core development and accessible to all fitness levels.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'REVERSE_CRUNCH_ON_A_BENCH', 'STRENGTH', '{core,abs,lower abs}', '{bench}', 'A reverse crunch performed lying on a bench, allowing for increased range of motion. Grip the bench behind your head and curl your hips up, bringing knees toward chest. The bench provides stability and allows hips to drop below parallel on the eccentric phase. This increased range builds lower ab strength more effectively than floor reverse crunches.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'REVERSE_CURL_AND_LIFT', 'STRENGTH', '{core,abs,lower abs}', '{bodyweight}', 'An advanced reverse crunch variation where you curl the hips up and then lift them straight up toward the ceiling. The lift phase adds an extra contraction at the top, increasing lower ab engagement. This double-action movement builds exceptional lower ab strength and control. Focus on controlled movement rather than momentum.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'ROTATIONAL_LIFT', 'STRENGTH', '{core,abs,obliques,rotation}', '{bodyweight}', 'A core exercise combining a crunch with a lifting and twisting motion. As you crunch up, rotate your torso and reach across your body with both arms. This movement engages the obliques and rectus abdominis while building rotational strength. The lift component adds range of motion. Excellent for functional core strength and athletic performance.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'SEATED_ALTERNATING_REVERSE_CRUNCH', 'STRENGTH', '{core,abs,lower abs,obliques}', '{bodyweight}', 'A reverse crunch performed from a seated position, alternating bringing each knee toward the chest. Sit with legs extended, lean back slightly, then pull one knee in at a time. The alternating pattern adds an oblique component and allows for higher rep training. The seated position challenges balance and core stability throughout.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'SEATED_LEG_U', 'STRENGTH', '{core,abs,lower abs,hip flexors}', '{bodyweight}', 'A dynamic core exercise where you sit and draw a "U" shape with your legs. From a seated position with legs extended, lift legs up and around in a U-pattern while maintaining core engagement. This movement combines hip flexion with rotational control. The continuous motion builds core endurance and hip mobility. Challenging for balance and coordination.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'SIDE_TO_SIDE_CRUNCH_AND_WEAVE', 'STRENGTH', '{core,abs,obliques}', '{bodyweight}', 'A dynamic crunch variation where you crunch to alternating sides in a weaving motion. As you crunch up, move side to side, engaging the obliques. The weaving pattern keeps constant tension on the core and challenges stability. This movement builds oblique strength and endurance while adding variety to standard crunch patterns.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'SINGLE_LEG_REVERSE_CRUNCH', 'STRENGTH', '{core,abs,lower abs,unilateral}', '{bodyweight}', 'A reverse crunch performed one leg at a time. Lying on your back, curl one hip up while keeping the other leg extended or grounded. This unilateral variation increases the challenge and helps identify imbalances between sides. The single-leg version requires more control and core stability than standard reverse crunches.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'SKATER_CRUNCH_CROSS', 'STRENGTH', '{core,abs,obliques,rotation}', '{bodyweight}', 'A dynamic crunch combining a skating motion with a cross-body crunch. From a crunch position, bring opposite elbow toward opposite knee in a skating pattern. This exercise mimics the lateral movement of speed skating while engaging the core through rotation. Excellent for athletes and building functional rotational strength.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('CRUNCH', 'STANDING_CABLE_CRUNCH', 'STRENGTH', '{core,abs,weighted}', '{cable machine}', 'A cable crunch performed while standing, pulling the cable down while crunching forward. This standing variation engages more stabilizer muscles than kneeling versions and can be more functional for athletes. The cable provides constant tension. Focus on spinal flexion rather than just bending at the hips. Great for variety in weighted ab training.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
