INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('DEADLIFT', 'BARBELL_DEADLIFT', 'STRENGTH', '{legs,glutes,hamstrings,lower back,traps,hinge pattern,compound}', '{barbell}', 'The king of all lifts - a fundamental compound movement lifting a loaded barbell from the floor to hip level. The conventional deadlift develops total-body strength, particularly the posterior chain (glutes, hamstrings, spinal erectors, traps). This exercise builds functional strength, improves posture, increases bone density, and elevates metabolism. Essential for any strength program.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('DEADLIFT', 'BARBELL_STRAIGHT_LEG_DEADLIFT', 'STRENGTH', '{legs,hamstrings,glutes,lower back,hinge pattern}', '{barbell}', 'Also known as the stiff-leg deadlift, performed with minimal knee bend to maximize hamstring and glute stretch. Lower the barbell by hinging at the hips while keeping legs relatively straight. This variation places greater emphasis on the hamstrings and less on the quads compared to conventional deadlifts. Excellent for posterior chain development and improving hip hinge mechanics.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('DEADLIFT', 'DUMBBELL_DEADLIFT', 'STRENGTH', '{legs,glutes,hamstrings,lower back,hinge pattern}', '{dumbbells}', 'A deadlift performed with dumbbells held at the sides instead of a barbell. This variation allows for a more natural hand position and can be easier on the lower back due to the weight distribution. The dumbbells provide greater freedom of movement and can help identify strength imbalances between sides. Excellent for beginners learning the hip hinge pattern or when barbells are unavailable.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('DEADLIFT', 'DUMBBELL_SINGLE_LEG_DEADLIFT_TO_ROW', 'STRENGTH', '{legs,hamstrings,glutes,back,lats,core,hinge pattern,unilateral,compound}', '{dumbbells}', 'A complex movement combining a single-leg Romanian deadlift with a rowing motion. Perform a single-leg RDL, then at the bottom position, row the dumbbells to your sides before returning to standing. This challenging exercise develops posterior chain strength, back development, balance, and coordination simultaneously. The combination of movements creates significant anti-rotation core demands.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('DEADLIFT', 'DUMBBELL_STRAIGHT_LEG_DEADLIFT', 'STRENGTH', '{legs,hamstrings,glutes,lower back,hinge pattern}', '{dumbbells}', 'The dumbbell version of the straight-leg deadlift, performed with minimal knee bend to emphasize hamstring stretch. The dumbbells allow for a natural hand position and can provide a deeper stretch than barbell versions. Lower the dumbbells along the front of the legs while maintaining a neutral spine. Excellent for hamstring development and improving hip mobility.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('DEADLIFT', 'KETTLEBELL_DEADLIFT', 'STRENGTH', '{legs,glutes,hamstrings,lower back,hinge pattern}', '{kettlebell}', 'A deadlift performed with one or two kettlebells held between the legs or at the sides. The kettlebell''s unique shape and center of mass create slightly different pulling mechanics than barbells. This variation is excellent for learning the hip hinge pattern and building posterior chain strength. Can be performed with single or double kettlebells.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('DEADLIFT', 'KETTLEBELL_FLOOR_TO_SHELF', 'STRENGTH', '{legs,glutes,hamstrings,lower back,core,hinge pattern}', '{kettlebell}', 'A kettlebell deadlift that finishes with the kettlebell in the front rack (shelf) position at shoulder level. Deadlift the kettlebell from the floor, then clean it to the rack position. This combination movement builds posterior chain strength while developing the clean movement pattern. Excellent for total-body conditioning and transitioning between ground and rack positions.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('DEADLIFT', 'KETTLEBELL_SUMO_DEADLIFT', 'STRENGTH', '{legs,glutes,hamstrings,adductors,inner thighs,hinge pattern}', '{kettlebell}', 'A sumo-stance deadlift performed with a kettlebell held between the legs. The wide stance with toes pointed outward emphasizes the glutes, adductors, and inner thighs more than conventional deadlifts. This variation allows for a more upright torso position, reducing lower back stress. The kettlebell''s handle provides a comfortable grip for the sumo pulling position.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('DEADLIFT', 'ONE_ARM_ONE_LEG_DEADLIFT', 'STRENGTH', '{legs,hamstrings,glutes,core,balance,hinge pattern,unilateral}', '{dumbbells,kettlebell}', 'An advanced single-leg deadlift variation using only one arm to hold the weight. Standing on one leg, hold a dumbbell or kettlebell in one hand and perform a Romanian deadlift while the opposite leg extends behind you. This extremely challenging exercise develops balance, posterior chain strength, grip strength, and anti-rotation core stability. The asymmetric loading creates significant stability demands.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('DEADLIFT', 'RACK_PULL', 'STRENGTH', '{legs,hamstrings,glutes,lower back,traps,hinge pattern,partial range}', '{barbell,power rack}', 'A partial-range deadlift performed from an elevated starting position using a power rack or blocks. The barbell starts at knee height or above, allowing heavier loads and emphasizing the lockout portion of the deadlift. Excellent for building top-end deadlift strength, trap development, and grip strength. Particularly useful for powerlifters working on lockout strength or those with mobility limitations.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('DEADLIFT', 'ROMANIAN_DEADLIFT', 'STRENGTH', '{legs,hamstrings,glutes,lower back,hinge pattern}', '{barbell}', 'The Romanian Deadlift (RDL) is the gold standard for hamstring development. Starting from standing, lower the barbell by pushing hips back with minimal knee bend, maintaining the bar close to the legs. The movement emphasizes the eccentric (lowering) phase and hip hinge pattern. RDLs build hamstring strength, improve hip mobility, and teach proper hinge mechanics essential for injury prevention.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('DEADLIFT', 'ROTATIONAL_DUMBBELL_STRAIGHT_LEG_DEADLIFT', 'STRENGTH', '{legs,hamstrings,glutes,core,obliques,rotation,hinge pattern}', '{dumbbells}', 'A straight-leg deadlift with an added rotational component. Perform a stiff-leg deadlift with dumbbells, then add a torso rotation at the bottom or top of the movement. This variation combines posterior chain development with rotational core strength. The rotation engages the obliques and teaches coordinated movement between the hips and spine, beneficial for athletic movements and functional fitness.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('DEADLIFT', 'SINGLE_ARM_DEADLIFT', 'STRENGTH', '{legs,hamstrings,glutes,lower back,core,grip,hinge pattern,unilateral}', '{dumbbells,kettlebell}', 'A deadlift performed with one arm holding a dumbbell or kettlebell. The asymmetric loading creates significant anti-rotation and anti-lateral flexion demands on the core. This variation builds grip strength, identifies strength imbalances, and develops unilateral pulling strength. The offset load challenges stability throughout the entire kinetic chain.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('DEADLIFT', 'SINGLE_LEG_BARBELL_DEADLIFT', 'STRENGTH', '{legs,hamstrings,glutes,balance,core,hinge pattern,unilateral}', '{barbell}', 'A single-leg Romanian deadlift performed with a barbell. Stand on one leg and hinge at the hip while the non-working leg extends behind for balance. This challenging variation develops single-leg strength, balance, posterior chain development, and hip stability. The barbell version is more challenging than dumbbell variations due to the need to balance the bar.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('DEADLIFT', 'SINGLE_LEG_BARBELL_STRAIGHT_LEG_DEADLIFT', 'STRENGTH', '{legs,hamstrings,glutes,balance,core,hinge pattern,unilateral}', '{barbell}', 'The straight-leg variation of single-leg barbell deadlift, emphasizing maximum hamstring stretch by keeping the working leg straighter throughout the movement. This increases the difficulty and hamstring emphasis compared to the standard single-leg version. Requires excellent balance, hamstring flexibility, and core control. One of the most challenging unilateral posterior chain exercises.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('DEADLIFT', 'SINGLE_LEG_DEADLIFT_WITH_BARBELL', 'STRENGTH', '{legs,hamstrings,glutes,balance,core,hinge pattern,unilateral}', '{barbell}', 'A Romanian deadlift performed standing on one leg with a barbell. This unilateral hinge pattern exercise challenges balance while building posterior chain strength. The non-working leg can be held in front, to the side, or extended behind. Excellent for identifying and correcting strength imbalances, improving balance, and developing single-leg strength for sports performance.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('DEADLIFT', 'SINGLE_LEG_RDL_CIRCUIT', 'STRENGTH', '{legs,hamstrings,glutes,balance,core,endurance,hinge pattern,unilateral}', '{bodyweight}', 'A bodyweight single-leg Romanian deadlift performed in circuit format for higher repetitions. Hinge on one leg while reaching toward the ground, extending the non-working leg behind. The circuit format emphasizes balance, muscular endurance, and movement quality over heavy loading. Excellent for warm-ups, active recovery, or high-volume training without equipment.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('DEADLIFT', 'SINGLE_LEG_ROMANIAN_DEADLIFT_CIRCUIT', 'STRENGTH', '{legs,hamstrings,glutes,balance,core,endurance,hinge pattern,unilateral}', '{bodyweight}', 'A circuit-style single-leg Romanian deadlift performed with bodyweight for higher volume. The Romanian deadlift form (starting from standing) performed on one leg challenges balance and posterior chain endurance. The circuit format makes this excellent for conditioning, movement preparation, or building work capacity. Can be progressed by adding tempo, pauses, or light weight.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('DEADLIFT', 'SINGLE_LEG_ROMANIAN_DEADLIFT_WITH_DUMBBELL', 'STRENGTH', '{legs,hamstrings,glutes,balance,core,hinge pattern,unilateral}', '{dumbbells}', 'One of the most effective unilateral posterior chain exercises. Stand on one leg holding dumbbells and perform an RDL by hinging at the hip, extending the non-working leg behind for balance. This exercise builds hamstring and glute strength, improves balance, develops hip stability, and addresses imbalances between legs. The dumbbells allow natural arm positioning and can be held in various positions (both arms, single arm, contralateral).');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('DEADLIFT', 'SUMO_DEADLIFT', 'STRENGTH', '{legs,glutes,hamstrings,adductors,inner thighs,quads,hinge pattern,compound}', '{barbell}', 'A deadlift variation with a wide stance and toes pointed outward, pulling with hands inside the legs. The sumo stance emphasizes glutes, adductors, and quads more than conventional deadlifts while reducing lower back stress. The wider stance and more upright torso make this variation popular among powerlifters and those with longer limbs. Excellent for glute development and hip strength.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('DEADLIFT', 'SUMO_DEADLIFT_HIGH_PULL', 'STRENGTH', '{legs,glutes,hamstrings,traps,shoulders,power,hinge pattern,full body}', '{barbell}', 'A dynamic movement combining a sumo deadlift with an upright row. Pull the barbell explosively from a sumo stance, then continue the momentum by pulling the bar high toward the chin with elbows leading. This full-body power exercise develops explosive hip drive, trap development, and cardiovascular conditioning. Popular in CrossFit and functional fitness training.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('DEADLIFT', 'TRAP_BAR_DEADLIFT', 'STRENGTH', '{legs,glutes,hamstrings,quads,traps,lower back,hinge pattern,compound}', '{trap bar}', 'A deadlift performed using a trap bar (hex bar) with handles at the sides. The neutral grip and centered weight distribution create a more natural pulling position and allow for a more upright torso. This variation is often more quad-dominant and lower back-friendly than conventional deadlifts. Excellent for building total-body strength, particularly for athletes or those with back sensitivities.');

INSERT INTO garmin_exercises (exercise_category, exercise_name, type, tags, equipment, description)
VALUES ('DEADLIFT', 'WIDE_GRIP_BARBELL_DEADLIFT', 'STRENGTH', '{legs,glutes,hamstrings,lower back,traps,upper back,grip,hinge pattern}', '{barbell}', 'A conventional deadlift performed with a snatch-width or wider grip on the barbell. The wide grip increases range of motion, requires greater starting position depth, and significantly increases upper back and trap involvement. This variation is commonly used by weightlifters to build pulling strength for the snatch. The extended grip demands exceptional grip strength and thoracic mobility.');